const api=wp.customize;export class WidgetSectionUpdate{constructor(){this.classControls=["bgtfw_header_layout","bgtfw_footer_layout"]}init(){return $(()=>this._onLoad()),this}_onLoad(){_.each(this.classControls,t=>this._bindSidebarControl(t))}_bindSidebarControl(t){api(t,e=>e.bind(()=>this.toggleSections(t)))}toggleSections(t){let e=t.includes("header")?"header":"footer",i=this.getActiveSections(e);this.deactivateSections(i),this.activateSections(t)}activateSections(t){let e=t.includes("header")?"header":"footer";if(t=api(t)(),!_.isEmpty(t))for(let i=1;i<=t;i++)api.section("sidebar-widgets-"+e+"-"+i).activate()}deactivateSections(t){_.each(t,function(t){api.section(t.id).deactivate()})}getActiveSections(t){return _.filter(wp.customize.panel("widgets").sections(),e=>e.active()&&e.id.includes(t))}}export default WidgetSectionUpdate;